import React, { useRef } from 'react';
import useFirebase from '../../hooks/useFirebase';

const AddOffers = () => {
    const offerNameRef = useRef();
    const priceRef = useRef();
    const descriptionRef = useRef();
    const imgRef = useRef();

    const { user } = useFirebase();

    const handleAddOffers = e => {
        const offerName = offerNameRef.current.value;
        const price = priceRef.current.value;
        const description = descriptionRef.current.value;
        const img = imgRef.current.value;

        const newOffer = { offerName, price , description, img};

        fetch('http://localhost:5000/new-offer', {
            method: 'POST',
            headers: {
                'content-type': 'application/json'
            },
            body: JSON.stringify(newOffer)
        })
            .then(res => res.json())
            .then(data => {
                if (data.insertedId) {
                    alert('Successfully added.')
                    e.target.reset();
                }
            })
        e.preventDefault();
    }
    return (
        <div>
            <h2>Please Service</h2>
            <form onSubmit={handleAddOffers}>
                <input type="text" ref={offerNameRef} />
                <input type="text" name="" id="" ref={priceRef} />
                <input type="text" name="" id="" ref={descriptionRef} />
                <input type="text" name="" id="" ref={imgRef} />
                <input type="submit" value="Add Offer" />
            </form>
        </div>
    );
};

export default AddOffers;
